      <InputGroup.Checkbox className="checkbox" onChange={handleCheck} />
      <InputGroup.Text>{name}</InputGroup.Text>
      <DropdownButton className="m-50px">
        <Button onClick={handleDelete}>bich</Button>
        <Button onClick={handleEdit}>bich</Button>
      </DropdownButton>
    </InputGroup>
  )
}
