import { useState, useRef } from "react";
import { Button, Form, InputGroup } from "react-bootstrap";
import TodoList from './TodoList';

export default function App() {

  const [todos, setTodos] = useState([{ name: 'wweeeeee ', checked: false },]);

  const addTodo = useRef();

  function handleAddTodo() {
    const newTodos = [...todos]
    const name = addTodo.current.value;
    if (name === "") {
      return
    }
    newTodos.push({ name, checked: false })
    setTodos(newTodos)
    addTodo.current.value = null
  }

  function checkTodo(id) {
    const newTodos = [...todos]
    const target = newTodos.find(({ todoId }) => todoId == id)
    target.checked = !target.checked
    setTodos(newTodos)
  }

  return (
    <div>
      <h1>There {todos.filter(({ checked }) => !checked).length} left todo!</h1>
      <InputGroup>
        <Form.Control ref={addTodo} type="text" maxLength={255} />
        <Button variant="secondary" onClick={handleAddTodo}>+</Button>
      </InputGroup>
      <TodoList todos={todos} />
    </div>
  );
}
