import { Button, Dropdown, DropdownButton, Form, InputGroup, ListGroup, ListGroupItem, } from "react-bootstrap";
import DropdownItem from "react-bootstrap/esm/DropdownItem";
import { useRef, useState } from 'react'

export default function Todo({ todo: { id, name, checked }, actions: { checkTodo, deleteTodo, editTodo } }) {

  function handleCheck() {
    checkTodo(id)
  }

  function handleDelete() {
    deleteTodo(id)
  }

  const [isEditing, setEditing] = useState(false)

  const editRef = useRef()

  function handleEditDone() {
    const newName = editRef.current.value
    if (newName === "") {
      return
    }
    editTodo(id, newName)
    setEditing(false)
  }

  return (
    //<ListGroup.Item className="todo-item" variant={checked ? "info" : "secondary"} >
    //  <Form.Check.Input className="checkbox" onChange={handleCheck} />
    //  {name}
    //  <Button className="mx-auto">gg</Button>
    //</ListGroup.Item>
    <InputGroup className="todo-item" bg={checked ? "info" : "secondary"} >
      {isEditing ?
        <>
          <Form.Control />
          <Button onClick={handleEditDone}>Done</Button>
        </>
        :
        <>
          <InputGroup.Checkbox className="checkbox" onChange={handleCheck} />
          <InputGroup.Text className="todo-text">{name}</InputGroup.Text>
          <DropdownButton className="m-50px">
            <Button onClick={handleDelete}>Delete</Button>
            <Button onClick={() => setEditing(true)}>Edit</Button>
          </DropdownButton>
        </>
      }
    </InputGroup>
  )
}
