import { Button, Dropdown, DropdownButton, Form, InputGroup, ListGroup, ListGroupItem, } from "react-bootstrap";
import { useState, useRef } from 'react'
import { AiFillEdit, AiFillDelete, AiOutlineMore } from 'react-icons/ai'
import { IoMdDoneAll } from 'react-icons/io'

export default function Todo({ todo: { id, name, checked }, actions: { checkTodo, deleteTodo, editTodo } }) {

  const [isEditing, setEditing] = useState(false)

  const [editText, setEditText] = useState(name)

  const [isMore, setMore] = useState(false)

  const editRef = useRef()

  function handleCheck() {
    checkTodo(id)
  }

  function handleDelete() {
    deleteTodo(id)
  }

  function handleEditDone() {
    if (editText === "") {
      return
    }
    editTodo(id, editText)
    setEditing(false)
  }

  return (
    <InputGroup
      className="todo-item"
      onBlur={() => setMore(false)}
      /*bg={checked ? "info" : "secondary"}*/ >
      {isEditing ?
        <>
          <Form.Control ref={editRef} onChange={(e) => setEditText(e.target.value)} value={editText} />

          <Button
            onClick={handleEditDone}
            variant="success" >
            <IoMdDoneAll />
          </Button>
        </>
        :
        <>
          <InputGroup.Checkbox
            className="checkbox"
            checked={checked}
            onChange={handleCheck} />
          <InputGroup.Text className="todo-text">{name}</InputGroup.Text>

          {isMore ? (
            <>
              <Button
                onClick={() => {
                  //editRef.current.focus()
                  setEditing(true)
                }}
                variant="warning"
              >
                <AiFillEdit />
              </Button>

              <Button
                onClick={handleDelete}
                variant="danger"
              >
                <AiFillDelete />
              </Button>
            </>
          ) : (
            <Button onClick={() => setMore(true)} >
              <AiOutlineMore />
            </Button>
          )
          }
        </>
      }
    </InputGroup>
  )
}
